import './App.css';
import { useState } from "react";
import Axios from 'axios';

function App() {
  const [name, setName] = useState("");
  const [age, setAge] = useState(0);
  const [country, setCountry] = useState("");
  const [position, setPosition] = useState("");
  const [wage, setWage] = useState(0);
  const [employeeList, setemployeeList] = useState([])

  const addEmployee = () =>{
    const URL = "http://localhost:3001/new"
    const body = {
      name: name, 
      age: age, 
      country: country,
      position: position,
      wage: wage
    }
    Axios.post(URL, body)
    .then(() => {
      setemployeeList([
        ...employeeList,
        {
          name: name, 
          age: age, 
          country: country,
          position: position,
          wage: wage,
        },
      ])
    })
    .catch(error => {console.log(error)})
  };

  const getEmployees = () =>{
    const URL = "http://localhost:3001/get"
    Axios.get(URL)
    .then(response => {setemployeeList(response.data)})
    .catch(error => {console.log(error)})
  }

  return (
    <div className="App">
      <div className="form">
        <label>Name: </label>
        <input type="text" onChange={(event => setName(event.target.value))}/>
        <label >Age: </label>
        <input type="number" onChange={(event => setAge(event.target.value))}/>
        <label >Country: </label>
        <input type="text" onChange={(event => setCountry(event.target.value))}/>
        <label >Position: </label>
        <input type="text" onChange={(event => setPosition(event.target.value))}/>
        <label >Wage: </label>
        <input type="number" onChange={(event => setWage(event.target.value))}/>
        <button onClick={addEmployee}>Add to database</button>
        <button onClick={getEmployees}>Show Data</button>
      </div>

      <div className="table-container">
        <table className="employees-table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Age</th>
              <th>Country</th>
              <th>Position</th>
              <th>Wage</th>
            </tr>
          </thead>
          <tbody>
          { 
            employeeList.map((val,key)=>{ return <tr key={val.id}>
                <td>{val.name}</td>
                <td>{val.age}</td>
                <td>{val.country}</td>
                <td>{val.position}</td>
                <td>{val.wage}</td>
                <td className="edit"><input className="table-input" type="number" /><button>Update wage</button></td>
              </tr>
            })
          }
          </tbody>
        </table>          
      </div>
    </div>
  );
}

export default App;
